                            name={item.name}
                            attribute={item.attribute}
                            level={item.level}
                            monsterType={item.monsterType}
                            atk={item.atk}
                            def={item.def}
                            index={item.index}
                            key={index}
                        />
                    )
                }}
            />
        </View>
    )
};

