import 'react-native-gesture-handler';
import React from 'react';
import { View, StyleSheet } from 'react-native';
import { Card, Divider, Text } from 'react-native-elements';

export default function DeckItem(props) {

    return (
        <View style={styles.container}>
            <Card containerStyle={styles.card}>
                <Card.Title style={styles.cardTitle}>{props.name}</Card.Title>
                <Divider />
                <Text>Attribute</Text>
                <Text>{props.attribute}</Text>
                <Text h4>Level</Text>
                <Text>{props.level}</Text>
                <Text h4>Monster Type</Text>
                <Text>{props.monsterType}</Text>
                <Text h4>Attack Power</Text>
                <Text>{props.atk}</Text>
                <Text h4>Defence</Text>
                <Text>{props.def}</Text>
            </Card>
        </View>
    )
}

const styles = StyleSheet.create({
    card: {
        width: '30%',
    },
    cardTitle: {
        fontSize: '25px'
    },
    container: {
        alignItems: 'center',
        justifyContent: 'center',
    }
})
