import 'react-native-gesture-handler';
import React from 'react';
import { View, StyleSheet } from 'react-native';
import { Card, Divider, Text } from 'react-native-elements';
import AddDeckCard from '../Profile/AddDeckCard';

export default function DeckItem(props) {

    return (
        <View style={styles.container}>
            <Card containerStyle={styles.card}>
                <Card.Title style={styles.cardTitle}>{props.name}</Card.Title>
                <Divider />
                <View style={styles.cardDetails}>
                    <Text style={styles.cardHead}>Attribute</Text>
                    <Text>{props.attribute}</Text>
                    <Text style={styles.cardHead}>Level</Text>
                    <Text>{props.level}</Text>
                    <Text style={styles.cardHead}>Monster Type</Text>
                    <Text>{props.monsterType}</Text>
                    <Text style={styles.cardHead}>Attack Power</Text>
                    <Text>{props.atk}</Text>
                    <Text style={styles.cardHead}>Defence</Text>
                    <Text>{props.def}</Text>
                </View>
            </Card>
        </View>
    )
}

const styles = StyleSheet.create({
    card: {
        width: '30%',
    },
    cardTitle: {
        fontSize: '24px',
    },
    cardHead: {
        fontSize: '18px',
        textDecorationLine: 'underline',
        marginTop: '10px'
    },
    cardDetails: {
        marginLeft: '25px',
        marginTop: '15px',
        marginBottom: '15px'
    },
    container: {
        alignItems: 'center',
        justifyContent: 'center',
    }
})
