import 'react-native-gesture-handler';
import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import { Button, Input } from 'react-native-elements';

export default function AddDeckCard(props) {
    const example = [
        {
            name: 'Blue Eyes White Dragon',
            attribute: 'Light',
            level: 8,
            monsterType: 'Dragon',
            atk: 3000,
            def: 2500,
            index: 1
        }
    ];

    const [card, setCard] = useState([
        {
            name: '',
            attribute: '',
            level: 0,
            monsterType: '',
            atk: 0,
            def: 0,
            index: 0
        }
    ]);
    //sets one card
    const [deck, setDeck] = useState([]);
    //sets the whole deck

    return (
        <View>
            <Input placeholder="Monster Name" onChangeText={setCard(card.name)} />
            <Input placeholder="Attribute" onChangeText={setCard(card.attribute)} />
            <Button title='Full Deck List' onPress={() => {
                props.navigation.navigate('Deck List', {example})
            }} />
        </View>
    )

}
